<?phprequire_once "../vendor/autoload.php";use MovieTracker\Controller\Controller;use MovieTracker\Controller\Error404Controller;// Ajustes para o cache do Navegadorheader("Cache-Control: no-cache, no-store, must-revalidate"); // Para HTTP 1.1header("Pragma: no-cache"); // Para HTTP 1.0header("Expires: 0"); // Para o navegador$routes = require __DIR__ . "/../config/routes.php";$pathInfo = $_SERVER["PATH_INFO"] ?? '/';$httpMethod = $_SERVER["REQUEST_METHOD"] ?? 'GET';session_start();$isLoginRoute = ($pathInfo === '/login') || ($pathInfo === '/register');if(!array_key_exists('loginStatus', $_SESSION) && !$isLoginRoute) {    header('Location: /login');    return;}if(array_key_exists('loginStatus', $_SESSION) && $isLoginRoute) {    header('Location: /');    return;}$pathKey = "$httpMethod|$pathInfo";if(array_key_exists($pathKey, $routes)) {    $controllerClass = $routes[$pathKey];    $controller = new $controllerClass();} else {    $controller = new Error404Controller();}/** @var Controller $controller */$controller->processRequest();