<?phpnamespace MovieTracker\Infra;use PDO;class Connection{    public static function getConnection(): PDO    {        $connection = new PDO("mysql:host=localhost;dbname=movie_tracker;port=3307", "root", "");        $connection->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        $connection->setAttribute(PDO::ATTR_DEFAULT_FETCH_MODE, PDO::FETCH_ASSOC);        return $connection;    }}