<?phpnamespace MovieTracker\Model;class User{    private ?int $id;    private string $email;    private string $password;    /**     * @param int|null $id     * @param string $email     * @param string $password     */    public function __construct(string $email, string $password, ?int $id = null)    {        $this->id = $id;        $this->email = strtolower($email);        if (password_get_info($password)['algo'] === 0 || is_null(password_get_info($password)['algo'])) {            $this->password = $this->encryptPassword($password);        } else {            $this->password = $password;        }    }    public function getId(): ?int    {        return $this->id;    }    public function setId(?int $id): void    {        $this->id = $id;    }    public function getEmail(): string    {        return $this->email;    }    public function getPassword(): string    {        return $this->password;    }    private function encryptPassword(string $password): string    {        return password_hash($password, PASSWORD_ARGON2ID);    }}